INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("7932", "baked stuffed mushrooms caps", "this recipe was adopted by me in february 2005 - i have revised this recipe to that of my mother's (a certified chef).  it is always a favorite in our home for parties and holiday gatherings and i hope you enjoy it too.", "40", "'60-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'occasion', 'appetizers', 'lunch', 'snacks', 'vegetables', 'dinner-party', 'holiday-event', 'dietary', 'christmas', 'new-years', 'thanksgiving', 'low-carb', 'mushrooms', 'low-in-something', 'superbowl'", "101.7, 13.0, 4.0, 4.0, 6.0, 19.0, 1.0", "10", "'remove mushroom stems from mushrooms', 'chop enough stems to measure 1 / 4 cup', 'place mushroom caps on a buttered baking sheet and set aside', 'in a medium skillet saut the chopped stems , onions , and garlic in oil', 'mix bread crumbs , bacon , parsley , oregano , salt and pepper to taste', 'add the dry ingredients , along with the melted butter and white wine to the skillet and mix well', 'shape mushroom mixture into 24 balls', 'mold each into the mushroom caps', 'sprinkle with cheese', 'bake 20 minutes at 350f'", "'mushrooms', 'butter', 'onion', 'dry white wine', 'dry breadcrumbs', 'cooked bacon', 'parsley', 'olive oil', 'garlic clove', 'oregano leaves', 'salt & pepper', 'monterey jack cheese'", "12", "https://www.food.com/recipe/7932");
