INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("8059", "hamburger pastry", "this is one of the zaar recipes that i adopted.  i hope to prepare this one soon and will post any modifications that i make to the recipe.", "0", "'15-minutes-or-less', 'time-to-make', 'main-ingredient', 'preparation', 'beef', 'eggs-dairy', 'vegetables', 'oven', 'dietary', 'ground-beef', 'meat', 'equipment'", "644.1, 86.0, 7.0, 23.0, 20.0, 110.0, 8.0", "12", "'heat the oven to 375 degrees f', 'cook and stir the meat and onion in a large skillet until the meat is brown', 'drain off the excess fat', 'remove from the heat and stir in the remaining ingredients except the pie crust mix and set aside', 'prepare the pastry as for a 2 crust pie as directed on the package', 'divide the pastry into 8 equal parts', 'roll each part , on a lightly floured surface , into a 7-inch circle', 'on half of the circle , spread about 1 / 4 cup of the meat mixture to within 1 / 2-inch of the edge', 'moisten the edge of the pastry with water and fold over the meat mixture , sealing the edges with a fork', 'place on an ungreased baking sheet and prick the tops with the fork', 'bake 30 to 35 minutes', 'you can serve these as sandwiches or , if you prefer , place on a plate and top with gravy or sauce'", "'beef', 'onion', 'canned vegetables', 'cheese', 'catsup', 'garlic salt', 'pepper', 'mustard', 'pie crust mix'", "9", "https://www.food.com/recipe/8059");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("8060", "carla s basic meatballs", "a snap to make and a breeze to freeze! this is a basic recipe for meatballs suitable for all uses. feel free to be creative and add any additional spices or flavors you need to suit your tastes.", "45", "'60-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'occasion', 'main-dish', 'side-dishes', 'beef', 'oven', 'potluck', 'freezer', 'dietary', 'seasonal', 'oamc-freezer-make-ahead', 'high-protein', 'low-carb', 'ground-beef', 'high-in-something', 'low-in-something', 'meat', 'to-go', 'equipment', 'number-of-servings'", "272.2, 19.0, 9.0, 15.0, 48.0, 25.0, 4.0", "12", "'mix ground beef , egg , onion , carrots , breadcrumbs , milk , worcestershire sauce , salt and freshly ground black pepper to taste together', 'shape mixture by tablespoonfuls into 1-1 / 2-inch balls', 'place the meatballs in a lightly greased 13x9x2-inch or 15-1 / 2x9-1 / 2x1-inch baking dish', 'bake , uncovered , in a 400 degree fahrenheit oven until light brown , about 20 to 25 minutes', 'drain off any excess fat', 'use meatballs as desired', 'freezing:', 'cover and allow meatballs to cool slightly before freezing', 'no special equipment is needed in the preparation for freezing', 'ziplock bags work fine for me', 'allow meatballs to thaw about 30 minutes at room temperature before using', 'use as desired with your favorite recipes'", "'lean ground beef', 'egg', 'onion', 'carrot', 'green pepper', 'italian seasoned breadcrumbs', 'milk', 'worcestershire sauce', 'salt', 'fresh ground black pepper'", "10", "https://www.food.com/recipe/8060");
