INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("8079", "mexican fiesta casserole", "i adopted this recipe in february 2005.  while i'm not much of a casserole person, this one looks to be pretty good.", "50", "'60-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'occasion', 'north-american', 'casseroles', 'main-dish', 'beef', 'vegetables', 'american', 'mexican', 'oven', 'dietary', 'one-dish-meal', 'seasonal', 'ground-beef', 'meat', 'taste-mood', 'equipment'", "511.1, 47.0, 32.0, 35.0, 49.0, 67.0, 10.0", "13", "'preheat the oven to 375f', 'cook the ground beef in a large skillet until browned', 'drain off the excess fat', 'season the meat with the salt and pepper', 'set aside', 'mix together the cheese , sour cream , mayonnaise and onion', 'set this mixture aside', 'stir the biscuit mix and water together until a soft dough forms', 'with floured fingers , pat the dough into the bottom of a greased 13 x 9 x 2-inch baking pan , pressing the dough about 1 / 2-inch up the sides of the pan', 'layer the browned ground beef , tomato slices , green pepper , and sliced olives over the dough layer', 'spoon the sour cream and cheese mixture over the top', 'bake , uncovered , until the edges of the dough are light brown , 25 to 30 minutes', 'cool 5 minutes and then cut into squares to serve'", "'lean ground beef', 'salt and pepper', 'chili powder', 'cheddar cheese', 'sour cream', 'mayonnaise', 'onion', 'bisquick baking mix', 'water', 'tomatoes', 'green bell pepper', 'black olives'", "12", "https://www.food.com/recipe/8079");
