INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("8081", "safari supper", "this is a recipe that i adopted in the recipezaar restaurant give-away of feb 2005. i've made it myself a couple times now and think the recipe is fine. i personally like to add about 1/3 cup of chopped green pepper, but it's not necessary--i'm happy to sponsor the recipe as originally written. cooking time shown is for the stovetop method, which is the one i've followed. i have yet to try the oven method, but according to the original cook it should take about 15-25 minutes longer.", "40", "'weeknight', '60-minutes-or-less', 'time-to-make', 'main-ingredient', 'cuisine', 'preparation', 'beef', 'fruit', 'vegetables', 'african', 'dietary', 'low-sodium', 'ground-beef', 'low-in-something', 'meat'", "314.6, 18.0, 34.0, 9.0, 41.0, 20.0, 10.0", "7", "'cook and stir the meat and onion in a large skillet until the onion is tender and the meat is brown', 'drain off the excess fat', 'stir in the remaining ingredients', 'to cook in a skillet: heat the mixture to boiling , then reduce the heat and simmer , covered , stirring occasionally , until the rice is tender , about 35 minutes', 'to cook in the oven: turn the mixture into an ungreased 2-quart casserole', 'cover and bake at 350 degrees f', ', stirring occasionally , until the rice is tender , about 50 to 60 minutes'", "'lean ground beef', 'onion', 'rice', 'water', 'chicken bouillon', 'curry powder', 'salt', 'ginger', 'cinnamon', 'raisins', 'peanut butter', 'honey'", "12", "https://www.food.com/recipe/8081");
