INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("8086", "ground beef   apple filled acorn squash halves", "great fall comfort food. the prep time is so long mainly because i have included the baking of the squash while you are preparing the other ingredients.  the cooking time is the final baking time.  enjoy!", "75", "'weeknight', 'time-to-make', 'main-ingredient', 'preparation', 'beef', 'fruit', 'vegetables', 'oven', 'dietary', 'low-cholesterol', 'low-calorie', 'healthy-2', 'ground-beef', 'low-in-something', 'apples', 'meat', 'equipment', '4-hours-or-less'", "449.3, 26.0, 101.0, 42.0, 49.0, 29.0, 17.0", "16", "'heat the oven to 400f', 'cut each of the squash in half and remove the seeds and fibers', 'place the squash , cut sides down , in an ungreased baking pan', 'then add water to the depth of 1 / 4-inch and bake , uncovered , until the squash is tender , about 30 to 40 minutes', 'while the squash is baking , cook and stir the meat in a large skillet until it is brown', 'drain off the excess fat', 'remove the skillet from the heat and stir in the 1 1 / 2 ts of salt as well as the cinnamon , the coarsely chopped tart apples and the raisins', 'when the squash is cooked , turn them so that the cut side is up and remove them to a platter', 'drain off any remaining liquid in the pan and dry', 'scoop out the pulp from the acorn squash , making a shell that is 1 / 4-inch thick all the way around', 'season the shells with salt to taste', 'mash the pulp and mix in the meat mixture', 'return to the shells , piling them full and sprinkle with 1 tb of brown sugar on each', 'drizzle with the melted butter', 'bake uncovered until the apple is tender , about 20 to 30 minutes', 'serve hot'", "'acorn squash', 'lean ground beef', 'salt', 'cinnamon', 'apples', 'raisins', 'brown sugar', 'margarine'", "8", "https://www.food.com/recipe/8086");
