INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("8255", "jiffy butter pie crust", "", "20", "'30-minutes-or-less', 'time-to-make', 'course', 'preparation', '5-ingredients-or-less', 'pies-and-tarts', 'desserts', 'easy', 'pies', 'dietary'", "342.5, 33.0, 0.0, 18.0, 9.0, 66.0, 10.0", "69", "'in a large bowl , combine flour and salt', 'with pastry blender or two knives , cut in butter until mixture resembles coarse crumbs', 'in small bowl , beat egg yolk with 3 tablespoons ice water', 'sprinkle mixture , 1 tablespoon at a time , over flour mixture', 'toss with fork to blend lightly but evenly', 'add up to 1 tablespoon water , if necessary , until dough begins to form a ball', 'knead dough gently 2 or 3 times', 'flatten into disk shape', 'to make single crust:on lightly floured surface , roll out half dough to 11 to 12"" circle', 'fit into 9"" pie plate', 'trim , leaving 1"" overhang', 'dampen underside of hangover with water', 'turn under', 'flute edges or make decorative edging as described below', 'place in freezer 10 minutes', 'any unused dough can be frozen , tightly wrapped , for up to 2 months', 'to make double crust:roll out dough as for single crust pie', 'fit into pie plate', 'trim', 'roll out remaining dough to 11 to 12"" circle', 'fit over top of pie', 'trim , leaving 1"" overhang', 'dampen edges of pastry with water', 'press together or turn under', 'flute edge', 'cut vents for steam in center of top crust', 'prebaked pie shell:prepare jiffy butter crust , recipe above , or one 10 or 11 ounce package piecrust mix according to package directions', 'roll out half dough', 'fit into 9"" pie plate', 'prepare single crust as directed', 'after fluting , place in freezer 10 minutes', 'heat oven to 425 degrees', 'line crust with foil', 'fill foil with dried beans or rice', 'bake 6 minutes', 'remove foil and beans', 'bake 8 to 10 minutes longer until pie shell is lightly browned', 'co