INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("8331", "assam spicy shrimp", "", "89", "'weeknight', 'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'occasion', 'appetizers', 'main-dish', 'seafood', 'asian', 'dinner-party', 'shrimp', 'dietary', 'spicy', 'stir-fry', 'shellfish', 'taste-mood', 'technique', '4-hours-or-less'", "290.7, 13.0, 105.0, 59.0, 44.0, 5.0, 10.0", "19", "'marinating: break tamarind apart , place in a small saucepan with water', 'simmer , covered , for about 10 minutes or until pasty coating separates from seeds', 'press through a sieve or food mill to extract all pulp and soupy liquid', 'discard seeds', 'rinse shrimps thoroughly , pat dry and marinate fro 1 hour with 1 tablespoon of this tamarind liquid , the oyster sauce and hot chili sauce', 'cooking: meanwhile , cut young ginger into toothpick-sized slivers', 'if using mature ginger , peel and sliver', 'peel and sliver garlic', 'place blachan in a non-stick pan and heat gently fro 3 to 4 minutes to develop the flavors', 'pell and cut red onions into wedges', 'combine oyster , soy and chili sauces with blachan', 'stir into remaining tamarind liquid', 'heat a large wok over high heat', 'when a drop of water sizzles immediately into steam pour in 2 tablespoons of the oil', 'swirl up sides of wok , and when just at smoking point , dump in ginger and garlic', 'stir fry until golden , about 3 to 4 minutes', 'adding more oil as needed , stir frey shrimp in batches until orange red , about 3 minutes', 'pour sauces over all the shrimp in the wok and cook at a brisk bubble over high heat until shrimps are aromatic and sauces have reduced and thickened to enrobe the shells , about 8 minutes', 'mix in sugar and onion wedges , taste to adjust seasoning , sweetening and spiciness'", "'water', 'shrimp', 'oyster sauce', 'hot chili sauce', 'tamarind pulp', 'young ginger root', 'garlic', 'blachan', 'red