INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("8336", "broiled chicken oregano", "lemony and herby marinated chicken, broiled with a nice butter glaze.", "0", "'15-minutes-or-less', 'time-to-make', 'main-ingredient', 'preparation', 'very-low-carbs', 'poultry', 'oven', 'broil', 'chicken', 'dietary', 'low-sodium', 'low-carb', 'low-in-something', 'meat', 'chicken-breasts', 'equipment'", "579.2, 75.0, 4.0, 8.0, 61.0, 86.0, 1.0", "13", "'several hours before serving or the day before , rub the chicken pieces pieces with the garlic and place them in a deep china or earthenware bowl', 'combine lemon juice , olive oil , salt and pepper to taste , and oregano and pour over chicken', 'cover the bowl and refrigerate the chicken for 1 hour , turning chicken pieces occasionally', 'when ready to cook , preheat the broiler to its highest setting', 'remove chicken from marinade', 'discard marinade', 'combine the melted butter , 1 / 3 cup lemon juice , and remaining oregano to make basting mixture', 'arrange the chicken pieces on the broiler rack and baste with a little of the melted butter mixture', 'reserve part of the basting mixture to serve over the chicken when served', 'broil the chicken 3-4 inches from the heat for 5 to 8 minutes per side , basting often', 'chicken is done when juices run clear when pierced with a sharp knife', 'remove to serving platter , pour reserved basting mixture over the chicken breasts and serve immediately', 'this is also good done on a bbq'", "'chicken breasts', 'garlic', 'lemon juice', 'olive oil', 'salt & pepper', 'fresh oregano', 'butter'", "7", "https://www.food.com/recipe/8336");
