INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("8352", "curried chicken and bow tie pasta salad", "", "50", "'60-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'salads', 'pasta', 'poultry', 'chicken', 'dietary', 'meat', 'pasta-rice-and-grains'", "1036.1, 86.0, 19.0, 11.0, 145.0, 78.0, 16.0", "15", "'poach chicken by placing in a large pot with enough cold water to barely cover', 'add peppercorns , bay leaf , parsley , thyme and one each: onion , carrot and celery cut into quarters', 'bring to a bare boil with lid on', 'reduce heat to low and simmer for 50 minutes', 'cool in poaching liquid , then drain , skin and shred', 'to make curry flavouring , soften diced onion in hot oil , then add curry powder and cook over medium-high heat for a further 3 minutes', 'add wine and water', 'boil rapidly until reduced to about 3 tablespoons', 'strain and let cool', 'prepare curry mayonnaise by combining all ingredients until smooth', 'place peppers in a large bowl , with chicken , pasta , salt , pepper and curry mayonnaise', 'mix gently until well- coated', 'if mayonnaise mixture is too thick , add warm water gradually until a smooth consistency is attained', 'place a portion of salad on bed of spinach and sprinkle reserved peppers on top', 'assemble just before serving , as the mayonnaise quickly loses its fresh look'", "'chicken', 'peppercorns', 'bay leaf', 'parsley', 'fresh thyme', 'onion', 'carrot', 'celery', 'green pepper', 'red pepper', 'yellow pepper', 'bow tie pasta', 'salt', 'pepper', 'vegetable oil', 'curry powder', 'white wine', 'water', 'mayonnaise'", "19", "https://www.food.com/recipe/8352");
