INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("8364", "italian meatballs in tomato sauce", "a delicious meatballs and sauce recipe!", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'occasion', 'condiments-etc', 'beef', 'european', 'italian', 'dietary', 'ground-beef', 'meat'", "418.7, 42.0, 56.0, 51.0, 27.0, 30.0, 9.0", "8", "'make the sauce: in a heavy saucepan cook the onion and the garlic in the oil over low heat , stirring , for 2 minutes', 'add parsley , basil , tomatoes , tomato paste , stock , wine , sugar , romano , salt , and oregano , and simmer the sauce , stirring occasionally , for 30 minutes', 'while sauce is cooking , prepare the meatballs: in a large bowl combine bread , chuck , eggs , romano , parsley , garlic , the oregano , salt , and pepper', 'mix well and form into 8 meatballs', 'in a large skillet brown the meatballs in the oil over high heat , turning them often', ""make sure to not crowd them in the pan , otherwise they won't brown properly"", 'transfer the browned meatballs to the sauce with a slotted spoon and simmer the mixture , stirring occasionally , for 30 minutes', ""you can serve them with your favorite pasta right away , but these improve in flavor if they're cooled and chilled , covered , and refrigerated overnight or up to 2 days before serving""", "'onion', 'garlic', 'olive oil', 'parsley', 'dried basil', 'tomatoes', 'tomato paste', 'chicken stock', 'dry red wine', 'sugar', 'romano cheese', 'salt', 'oregano', 'bread', 'chuck', 'eggs', 'dried oregano', 'black pepper'", "18", "https://www.food.com/recipe/8364");
