INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("8368", "marions best ever apple pie", "--adopted recipe--", "95", "'time-to-make', 'course', 'main-ingredient', 'preparation', 'low-protein', 'healthy', 'pies-and-tarts', 'desserts', 'fruit', 'oven', 'pies', 'dietary', 'low-sodium', 'low-in-something', 'apples', 'equipment', '4-hours-or-less'", "326.3, 15.0, 111.0, 3.0, 8.0, 29.0, 19.0", "20", "'to make pastry , cut butter and lard into flour with pastry blender until mixture resembles coarse crumbs', 'scatter water on mixture , mixing with fork until dough forms a ball', 'wrap in plastic wrap and chill about 30 minutes', 'divide into 2 balls', 'roll out one portion to fit a 9 inch pie pan , leaving 1 inch of pastry at edge', 'preheat oven to 425 degrees f', 'core , peel and thinly slice apples', 'toss with cinnamon , sugar and lemon juice in a bowl', 'sprinkle bread crumbs evenly over pie shell', 'arrange apples slices on top , leaving as little space between slices as possible', 'pile slices high but do not mound in centre', 'roll out other half of dough to fit top of pie', 'brush edge of bottom crust with egg wash made by blending beaten egg yolk with milk', 'place top layer of dough on pie', 'tuck top layer under bottom layer of dough around edge of pie', 'crimp edges with fingers to form fluting', 'prick pie with fork in at least a dozen places to let steam escape', 'brush with eggwash', 'bake 15 minutes', 'reduce oven temperature to 400 degrees f and bake 15 minutes , then reduce to 325 degrees f and bake 20 minutes more , or until crust is golden brown'", "'all-purpose flour', 'butter', 'water', 'tart apples', 'ground cinnamon', 'brown sugar', 'breadcrumbs', 'egg yolk', 'milk'", "9", "https://www.food.com/recipe/8368");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("8369", "west african meat pies patties", "", "0", "'weeknight', '15-minutes-or-less', 'time-to-make', 'main-ingredient', 'preparation', 'pasta', 'pork', 'oven', 'meat', 'pasta-rice-and-grains', 'equipment'", "387.6, 31.0, 9.0, 34.0, 27.0, 58.0, 12.0", "11", "'mix all spices , diced bell peppers and diced onion , with ground meat thoroughly , and brown the meat in 1 teaspoon of olive oil in a skillet until meat is cooked', 'once ground beef is cooked , add the diced cooked potatoes and heat for 5 minutes', 'taste meat mixture and adjust amount of spices according to taste', 'to make dough: sift together dry ingredients and cut the butter into the dry ingredients with a fork or pastry cutter until mixture is crumbly', 'while mixing with a wooden spoon , add water a tablespoon at a time until dough holds together in a ball', 'when dough comes together into a ball , lightly flour your work surface and roll out the dough until 1 / 8 inch thick', 'cut out 6-8 inch circles', 'place two teaspoons of meat stuffing in ceter of pastry cut outs', 'moisten the edges of the dough with water and fold the top half of the dough circle over the filling and pinch edges together with a fork to seal', 'once all patties are formed , lightly brush the tops with beaten egg', 'bake on a lightly greased cookie sheet at 350 degrees for 35 minutes or until golden brown'", "'small potatoes', 'lean ground beef', 'frozen mixed vegetables', 'onion', 'mixed red and green sweet pepper', 'salt', 'black pepper', 'onion powder', 'garlic powder', 'beef bouillon cube', 'butter', 'flour', 'baking powder', 'ice water', 'egg'", "15", "https://www.food.com/recipe/8369");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("8370", "just another chocolate cake", "this is one of my adopted recipes. i finally got around to making this. this is the first choc cake i've made from scratch in over 40 years, i discovered box mixes and never looked back. since i adopted this orphan i decided i should make it. it seems fine to dh and me. but how would i know? i used kittencals frosting recipe #89207. i baked in a 9x13 inch pan, the original recipe calls for 3 pans but i don't have 3 pans. i left the original recipe instructions for those who prefer layer cakes. i think 50 min baking time for mine was about right.", "40", "'60-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'for-large-groups', 'desserts', 'oven', 'cakes', 'chocolate', 'dietary', 'equipment', 'number-of-servings'", "247.4, 11.0, 103.0, 7.0, 7.0, 11.0, 14.0", "16", "'combine cocoa , hot water and baking soda in a small bowl', 'let stand while mixing other ingredients', 'cream the shortening and sugar together in a mixing bowl , using an electric mixer set on medium speed , until light and fluffy', 'add eggs , one at a time , beating well after each addition', 'beat in vanilla and cocoa mixture', 'add flour alternately with the buttermilk to creamed mixture beating well after each addition', 'pour batter into 3 greased and wax paper lined 8-inch round cake pans', 'bake in a preheated 350 degree f', 'oven for 25 minutes or until cakes test done', 'cool in pans on racks for 10 minutes', 'remove from pans', 'cool completely on racks', 'to assemble cake , place one cake layer on serving plate', 'spread with frosting of your choice', 'top with second cake layer and spread a layer of frosting', 'top with third cake layer and frost sides and top with remaining frosting'", "'cocoa', 'hot water', 'baking soda', 'vegetable shortening', 'sugar', 'eggs', 'vanilla extract', 'flour', 'buttermilk'", "9", "https://www.food.com/recipe/8370");
