INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("8374", "savory oven french fries", "oh these are so simple and good! the salad dressing mix gives them a savory flavor.  enjoy!", "55", "'60-minutes-or-less', 'time-to-make', 'main-ingredient', 'cuisine', 'preparation', 'occasion', '5-ingredients-or-less', 'potatoes', 'vegetables', 'oven', 'easy', 'european', 'dietary', 'taste-mood', 'savory', 'equipment'", "306.5, 10.0, 10.0, 0.0, 12.0, 4.0, 18.0", "10", "'preheat oven to 350f', 'peel potatoes and slice for french fries', 'lay out on paper towel , pat dry', 'potatoes should be as dry as possible', 'put potatoes in a large bowl and drizzle with oil , tossing to coat evenly', 'lay in a single layer on a large baking sheet', 'sprinkle dry dressing mix and parsley over all', 'bake 25 min , then flip over only once', 'return to oven and increase temperature to 450f', 'cook 3 to 5 min longer until potatoes are tender and start to brown'", "'potatoes', 'good seasons italian dressing', 'oil', 'fresh parsley'", "4", "https://www.food.com/recipe/8374");
